/*Table structure for table `comment` */

DROP TABLE IF EXISTS `comment`;

CREATE TABLE `comment` (
  `cid` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '评论自增id',
  `ctid` bigint(20) NOT NULL COMMENT '所属帖子',
