/*Table structure for table `section` */

DROP TABLE IF EXISTS `section`;

CREATE TABLE `section` (
  `sid` int(11) NOT NULL AUTO_INCREMENT COMMENT '板块自增Id',
  `sname` varchar(20) NOT NULL COMMENT '板块名字',
  `smasterid` bigint(20) NOT NULL COMMENT '对于user表的用户id',
  `sstatement` varchar(300) NOT NULL COMMENT '详细描述',
  `sshortsm` varchar(30) DEFAULT NULL COMMENT '简要描述',
  `sclickcount` bigint(20) DEFAULT '0' COMMENT '访问量',
  `stopiccount` bigint(20) DEFAULT '0' COMMENT '帖子量',
  `sparentname` varchar(20) NOT NULL DEFAULT '0' COMMENT '父级菜单名称',
  PRIMARY KEY (`sid`),
  KEY `smasterid` (`smasterid`),
  CONSTRAINT `section_ibfk_1` FOREIGN KEY (`smasterid`) REFERENCES `user` (`uid`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=101 DEFAULT CHARSET=utf8;

/*Data for the table `section` */

insert  into `section`(`sid`,`sname`,`smasterid`,`sstatement`,`sshortsm`,`sclickcount`,`stopiccount`,`sparentname`) values (1,'技术问答',1,'把你的问题提出来，给大神解答吧！','有什么问题来这里提吧',24,2,'0'),(2,'java',1,'Java是一门面向对象编程语言，不仅吸收了C++语言的各种优点，还摒弃了C++里难以理解的多继承、指针等概念，因此Java语言具有功能强大和简单易用两个特征。Java语言作为静态面向对象编程语言的代表，极好地实现了面向对象理论，允许程序员以优雅的思维方式进行复杂的编程','java编程',46,6,'技术分享'),(3,'c/c++ ',1,'C语言是在70年代初问世的。一九七八年由美国电话电报公司(AT&T)贝尔实验室正式发表了C语言。同时由B.W.Kernighan和D.M.Ritchit合著了著名的“THE C PROGRAMMING LANGUAGE”一书。通常简称为《K&R》，也有人称之为《K&R》标准。但是，在《K&R》中并没有定义一个完整的标准C语言，后来由美国国家标准学会在此基础上制定了一个C 语言标准，于一九八三年发表。通常称之为ANSI C','c/c++编程',61,0,'技术分享'),(4,'python',1,'Python具有丰富和强大的库。它常被昵称为胶水语言，能够把用其他语言制作的各种模块（尤其是C/C++）很轻松地联结在一起。常见的一种应用情形是，使用Python快速生成程序的原型（有时甚至是程序的最终界面），然后对其中[3]  有特别要求的部分，用更合适的语言改写，比如3D游戏中的图形渲染模块，性能要求特别高，就可以用C/C++重写，而后封装为Python可以调用的扩展类库。需要注意的是在您使用扩展类库时可能需要考虑平台问题，某些可能不提供跨平台的实现。','python编程',38,0,'技术分享'),(5,'情感部落',1,'在这里你可以宣泄一切情感遭遇，可以深情表白，还可以晒情侣幸福合照。情感','感情的碰撞',25,0,'情感'),(6,'真情天下',1,'深夜，安静的角落，听听心里的声音','天下有真情',22,0,'情感'),(7,'婆媳关系',1,'复杂的婆媳关系','婆媳关系',10,0,'情感'),(8,'仗剑杂谈',1,'本论坛的杂谈','本论坛的杂谈',16,1,'杂谈'),(9,'医疗曝光',1,'医疗曝光','医疗的曝光',15,0,'杂谈'),(10,'大话教育',1,'教育对于一个人的成长很重要','说说学校与家庭的教育',15,1,'杂谈'),(11,'娱乐八卦',1,'看看身边乃至娱乐圈的八卦','我们一起来八卦',10,0,'娱乐'),(12,'体育沙龙',1,'体育最近状况咱们来聊聊','体育体育',7,0,'娱乐'),(13,'星座论坛',1,'你是什么座，我们般配吗','你是什么座，我们般配吗',7,0,'娱乐'),(14,'保健养生',1,'保健养生','保健养生',36,0,'生活'),(15,'文化漫谈',1,'各路各国文化杂谈','探究文化',13,0,'生活'),(16,'手机综艺',1,'手机的各种资料','手机综艺',6,0,'生活'),(19,'英雄联盟',1,'《英雄联盟》(简称LOL)是由美国拳头游戏(Riot Games)开发、中国大陆地区腾讯游戏代理运营的英雄对战MOBA竞技网游。\n游戏里拥有数百个个性英雄，并拥有排位系统、天赋系统、符文系统等特色养成系统。\n《英雄联盟》还致力于推动全球电子竞技的发展，除了联动各赛区发展职业联赛、打造电竞体系之外，每年还会举办“季中冠军赛”“全球总决赛”“All Star全明星赛”三大世界级赛事，获得了亿万玩家的喜爱，形成了自己独有的电子竞技文化。','一起战斗吧',29,1,'网络游戏'),(100,'箐箐校园',1,'箐箐校园','箐箐校园',58,702,'南昌航空大学');

/*Table structure for table `sixin` */

DROP TABLE IF EXISTS `sixin`;

CREATE TABLE `sixin` (
  `siid` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '自增私信id',
  `sifromuid` bigint(20) NOT NULL COMMENT '为了让系统发消息，-1代表系统，不用外键',
  `sitouid` bigint(20) NOT NULL COMMENT '接受方',
  `content` text NOT NULL COMMENT '为了丰富以后内容是由text',
  `time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '发送时间',
  `isread` int(11) DEFAULT '0' COMMENT '0默认未读，1已读',
  PRIMARY KEY (`siid`),
  KEY `sifromuid` (`sifromuid`),
  KEY `sitouid` (`sitouid`),
  CONSTRAINT `sixin_ibfk_2` FOREIGN KEY (`sitouid`) REFERENCES `user` (`uid`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

/*Data for the table `sixin` */

insert  into `sixin`(`siid`,`sifromuid`,`sitouid`,`content`,`time`,`isread`) values (1,-1,2,'<p style=\'color:grern\'>欢迎使用仗剑论坛，有你的世界更精彩!<br/>  <small> 站长：李胜助</small></p>','2017-05-16 09:30:05',1),(2,-1,3,'<p style=\'color:grern\'>欢迎使用仗剑论坛，有你的世界更精彩!<br/>  <small> 站长：李胜助</small></p>','2017-05-16 09:34:38',1),(3,1,2,'sfsdfsdf','2017-05-16 14:47:06',1),(4,1,2,'dasdasf','2017-05-16 14:47:11',1),(5,1,2,'855','2017-05-16 15:19:13',1),(6,-1,4,'<p style=\'color:grern\'>欢迎使用仗剑论坛，有你的世界更精彩!<br/>  <small> 站长：李胜助</small></p>','2017-06-06 03:10:54',1);

/*Table structure for table `topic` */

DROP TABLE IF EXISTS `topic`;

CREATE TABLE `topic` (
  `tid` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '帖子自增id',
  `tsid` int(11) NOT NULL COMMENT '所属板块',
  `tuid` bigint(20) NOT NULL COMMENT '发帖人',
  `ttopic` varchar(50) NOT NULL COMMENT '主题',
  `tcontent` longtext NOT NULL COMMENT '内容',
  `ttime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '发布时间',
  `treplaycount` bigint(20) DEFAULT '0' COMMENT '回帖量',
  `tclickcount` bigint(20) DEFAULT '0' COMMENT '点击量',
  `tlastclicktime` datetime DEFAULT NULL COMMENT '上次访问时间',
  `tstaus` int(11) DEFAULT '0' COMMENT '0不置顶1置顶',
  `tzan` bigint(20) DEFAULT '0' COMMENT '点赞量',
  PRIMARY KEY (`tid`),
  KEY `tsid` (`tsid`),
  KEY `tuid` (`tuid`),
  KEY `ttopic` (`ttopic`),
  CONSTRAINT `topic_ibfk_1` FOREIGN KEY (`tsid`) REFERENCES `section` (`sid`) ON DELETE CASCADE,
  CONSTRAINT `topic_ibfk_2` FOREIGN KEY (`tuid`) REFERENCES `user` (`uid`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=16728 DEFAULT CHARSET=utf8;

/*Data for the table `topic` */

insert  into `topic`(`tid`,`tsid`,`tuid`,`ttopic`,`tcontent`,`ttime`,`treplaycount`,`tclickcount`,`tlastclicktime`,`tstaus`,`tzan`) values (24,2,1,'大苏打实打实','上嘀咕嘀咕嘀咕受到收到个大股东干到干到','2017-04-11 13:52:07',0,6,NULL,0,0),(25,2,1,'嘎嘎嘎嘎嘎','\n                                            <h3>你好 </h3>\n                                            <p>这里写你的文章固定格式是</p>\n                                        ','2017-04-11 13:52:14',3,37,NULL,0,0),(27,2,1,'父子窗口','\n                                            <h2>layer弹窗插件</h2><div><ol><li>&nbsp; &nbsp;layer ui<br></li><li>layer date</li><li>layer im</li></ol></div>\n                                        ','2017-04-25 09:22:20',0,40,NULL,0,0),(29,10,1,'高考考生家长的五大“不要','\n                                            <p><span style=\"font-family: Arial; color: rgb(0, 0, 0);\">2015年高考日益临近，为了给迎考的孩子们帮忙，家长们又开始忙活起来了。可是家长如果帮忙过了头，就会好心办坏事，加剧孩子的心理紧张，加重孩子的心理压力，妨碍孩子的正常发挥。据应用心理学研究员、知名心理咨询师马志国分析，家长帮忙帮过头有这样几种表现：期望过高、关注过度、管束过严、唠叨过多和保护过分。帮忙过头的结果往往会影响孩子高考正常发挥。</span></p><h3><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; font-size: 14px;\"><span style=\"font-family: Arial; color: rgb(0, 0, 0);\">　　家长不要期望过高</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; font-size: 14px;\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 0);\">　　【心理解析】家长的期望过高，超出了孩子的能力，就会给孩子造成压力。很多孩子感到考试压力很大，对自己失去信心，父母的过高期望是个直接的影响因素。面临高考，家长的这种过高期望，就更赤裸裸地表现为盼孩子考重点和读名校了。有的家长常常流露出对名牌大学的羡慕，孩子成绩本来在一本二本之间，却不断地给孩子说：再努力一下，就有上名牌的希望；还有的家长硬让孩子冲清华，结果却连第二志愿高校也考不上。</span></span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; font-size: 14px;\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 0);\">　　【心理支招】当我们以一颗平常心待孩子的时候，我们就会发现孩子身上有那么多的闪光点，我们就会给孩子更多的肯定评价，给孩子的心灵注入更多的自信。肯定评价哪里来？只有期望降下来，评价才能提上去，才能给孩子积极的肯定。孩子面对高考了，家长正确评价孩子的态度更为重要。其实，到了这个时候，孩子掌握的知识基本已成定局，短时间内不可能有质的飞跃。家长把期望降下来，实事求是地甚至降格设定目标，心里就会多一分坦然。这样，有助于孩子坦然面对高考，有助于孩子增强自信，有助于孩子考场上更好地临场发挥。</span></span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　家长不要关注过度</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　【心理解析】面临大考，孩子们的压力是怎么造成的？就是由于太拿考试结果当回事，考前复习可能过度疲劳，考场情绪可能过分紧张，从而超过了大脑能承受的限度。于是，到了答题时，大脑就出现了超限抑制，脑细胞就会自动停止工作不再听从使唤了，也就影响了考前复习和临场考试的效果。这种情况下，家长对孩子高考的过度关注引发的种种焦虑心态，又必然传染给孩子，让孩子更焦虑，在复习中和考场上更容易出现超限抑制，更容易考砸了。</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　【心理支招】作为家长需要坚持的一个原则是：如果真爱你的孩子，就别太拿孩子的考试结果当回事，对孩子的高考来点平常心，既不要过于紧张，也不要故作轻松，给孩子营造一个平常的学习环境和家庭氛围。所以，你该上班上班，该做什么做什么，便是最好。家长这样的心态，也会传染给孩子。孩子受到来自家长的平常心的感染，就会把考试看得淡些再淡些，复习起来就会心清气爽，面临高考就会处之泰然。</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　家长不要管束过严</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　【心理解析】这样管束过严，使孩子在家中的学习很难有一种平静的心态，家庭成了学校紧张生活的延续，孩子心理没有了缓冲的时间。结果呢，可能让孩子感觉没有自由的空间，可能让孩子缺少自主复习的时间，可能束缚了孩子学习的主动性和积极性，甚至可能让孩子产生逆反心理与家长对立。</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　【心理支招】家长要有一个明确的角色定位，不管怎么说，孩子才是考试的主角，家长只能当配角。我们要相信，面对大考，作为主角的孩子对于该怎么学自己心里是有数的，何况还有学校老师详尽的指导。即使有的家长当年自己考过大学，那也是过去的事了，所谓经验也会变成老黄历。所以，最现实的做法是，家长要把学习的主动权还给孩子，对孩子的考前复习不要多管，不要多操心。至于复习资料之类的事情，更有老师安排，家长更不必瞎操心。</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　家长不要唠叨过多</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　【心理解析】考前家长们的唠叨一般分为两种。一是反映了家长的攀比心理。比如说：某某同学平时成绩跟你差不多，你可不能输给他，再比如说：某某去年考上重点了，你要是像他一样就好了。一个女孩说：我一打电话问同学问题，爸妈听见就说我连这么简单的题都不会做。其实，他们不了解情况还要发言。弄得我心情很烦，压力也很大。我希望父母在我面前少说别人的孩子聪明，说自己的孩子太笨。一个男孩说：每次考试，爸妈都很紧张，总是问个不停，总是嘱咐个不停，我感觉他们心里比我还焦虑。</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　唠叨的结果，是必然增加孩子的心理压力，让孩子感到不被信任，造成孩子厌烦的心理，导致孩子对家长的反感，甚至还会因为心情不好而对家长发脾气。</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　【心理支招】家长朋友要练就一个功夫：在孩子大考前几天，要学会管住自己的嘴，说句极端的话——打死也不唠叨。即便是表达关心，也要看好时机。有些必要的嘱咐，最好“说一不二”，不重复第二遍。</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　家长如果变唠叨为倾听就更棋高一着了。考前和孩子相处时，家长不妨抽空带孩子散散步，和孩子聊聊天。和孩子聊天的时候，如果孩子向你诉说困难或压力，可能只是一种情绪的宣泄，一种对你的信任，并不意味着马上要解决。这时候你需要做的是，多贡献耳朵，当个好听众。如果你还想说话，有这唠叨的工夫，不如给孩子讲讲笑话，彼此都放松一下心情。</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　家长不要保护过分</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　【心理解析】孩子要大考了，吃啊睡啊，父母自然牵肠挂肚，但是，过度关爱和保护，会弄得家里的空气都紧张起来。这种过度紧张的家庭气氛，反会加重孩子的焦虑感，给孩子帮倒忙。</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　先说睡吧。有些家长恐怕孩子考前睡不好误了事，于是老早张罗孩子上床睡觉。还有的家长盲目让孩子调整什么睡眠，刻意地规定睡眠时间。孩子呢，就这样硬性地躺在床上，强迫自己入睡，总想着怎样才能睡觉。结果呢？灵得很，肯定睡不着。如今营养补剂五花八门，迄今还没有哪一位营养学家说过这些东西可以提高考试成绩。倒是有考生说：别人都喝我不喝，怎么考得过别人？家长买了很多补品，考不好更对不起爸爸妈妈。</span></p><p style=\"word-wrap: break-word; padding: 0px; margin-top: 10px; margin-bottom: 0px; color: rgb(0, 0, 0); font-size: 14px;\"><span style=\"font-family: Arial;\">　　【心理支招】家长该怎样照顾孩子考前的生活？关于睡，家长应该指导孩子这样做：困就睡，不困就学，躺在床上睡得着就睡，睡不着就随便翻翻书，只是别看小说。反正睡好睡不好不碍什么事。结果呢？入睡反倒容易得多。如果考前真的睡不好呢？那也无所谓。孩子考前如果说没睡好，你该鼓励他挺起胸，满怀信心地走进考场。</span></p></h3>\n                                        ','2017-04-26 21:18:26',0,23,NULL,0,0),(32,2,1,'Spring','<h3 style=\"border: 0px; margin: -8px 0px 20px; padding: 0px; font-size: 20px; font-stretch: normal; line-height: 30px; font-family: &quot;Microsoft YaHei&quot;, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(0, 0, 0);\">一、Spring简介</h3><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">1.Spring简化Java开发</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">Spring Framework是一个应用框架，框架一般是半成品，我们在框架的基础上可以不用每个项目自己实现架构、基础设施和常用功能性组件，而是可以专注业务逻辑。因此学习Spring Framework在架构和模式方面的结构和原理，对我们在架构和模块级别的理解帮助极大。Spring Framework（参考1）的宗旨是简化Java开发，主要的手段如下：</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">（1）在架构上解耦：通过DI（依赖注入）管理类型依赖，通过AOP分离关注点，减少重复代码。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">（2）在设计上广泛采用DIP（依赖倒置）和ISP（接口隔离）等原则和Facade（外观）等模式：提供简化的调用接口并封装了众多出色的第三方组件。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">（3）在语言层面上采用注解：通过配置文件和Annotation（参考.NET Attribute）简化应用配置。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">2.Spring Framework的架构和模块：</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">Spring Framework本身的架构是典型的松散分层，外层可以按需引用全部内层，内层不能引用外层。Spring的基础组件如下图所示：</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><img class=\"aligncenter\" src=\"http://images2015.cnblogs.com/blog/194803/201512/194803-20151223205733484-552517654.png\" alt=\"\" style=\"margin: 0px; padding: 0px; font-size: 0px; color: transparent; text-align: center; max-width: 100%; height: auto;\"></p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">从图中可以看出，开始的模块只有从core\\beans\\aop\\context四个组件，后来添加了context-support【1.2】扩展模块、expression【3.0】扩展模块和beans-groovy【4.0】扩展模块。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">Spring上述模块的基础上，内建和封装了众多的实用的通用组件，主要的组件如图所示：</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><img class=\"aligncenter\" src=\"http://images2015.cnblogs.com/blog/194803/201512/194803-20151223210129249-379081934.png\" alt=\"\" style=\"margin: 0px; padding: 0px; font-size: 0px; color: transparent; text-align: center; max-width: 100%; height: auto;\"></p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">从图中可以看出，spring-oxm、spring-jdbc和spring-web是众多模块依赖的核心，spring-oxm提供了Object和XML的映射支持。</p><h3 style=\"border: 0px; margin: -8px 0px 20px; padding: 0px; font-size: 20px; font-stretch: normal; line-height: 30px; font-family: &quot;Microsoft YaHei&quot;, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(0, 0, 0);\">二、基础知识</h3><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">1.DIP：DIP(依赖倒置原则)是DI(依赖注入)的核心（参考2）。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">（1）高层模块不应该依赖于低层模块。两者都应该依赖于抽象。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">（2）抽象不应该依赖于细节。细节应该依赖于抽象。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">说人话就是：将对具体类的引用转换成对其接口的引用，具体类只引用接口（引用==依赖，接口==接口或抽象类）。事实上我们调用具体类的时候在头脑里也是只关心其提供的API而非实现，DIP则通过在设计和<span class=\"wp_keywordlink\" style=\"border: 0px; margin: 0px; padding: 0px;\"><a href=\"http://www.amazon.cn/gp/product/B003BY6PLK/ref=as_li_qf_sp_asin_il_tl?ie=UTF8&amp;tag=importnew-23&amp;linkCode=as2&amp;camp=536&amp;creative=3200&amp;creativeASIN=B003BY6PLK\" title=\"重构\" rel=\"nofollow\" target=\"_blank\" class=\"external\" style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(0, 153, 204);\">重构</a></span>阶段在技术手段上保证了解耦。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">2.DI：DI（依赖注入）让我们不必手写工厂代码来管理接口和实现类的映射、对象的创建和生命周期的管理。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">（1）接口注入：必须实现特定的接口才可以，侵入性太强，现在已经无人关心和使用。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">（2）构造函数注入：依赖体现在构造函数的参数上。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">（3）属性注入：依赖体现在属性上。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">由于在实现时，可以将类型注册为自己的兼容类型，这样依赖注入就可以直接替代new实例化对象，这样理解和使用依赖注入工具还不如不使用或手写工厂了。依赖注入工具在实现时肯定会实现成一个支持不同配置和不同生命周期的对象工厂，但即使没有提供一套添加依赖倒置原则限制的API，也不意味着我们把它当成new的替代品。如同映射工具虽然在实现时可以任意映射，但不是用来取代赋值的，而是用来处理领域实体和视图模型等有实际对应关系的对象之间的映射。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">（1）依赖配置：依赖配置是依赖注入实现的基础。依赖注入工具都至少支持代码配置和文件配置。Java中可以通过Annotation（.NET中通过Attribute）简化配置。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">（2）对象工厂：根据配置返回一个或多个对象。这是核心功能。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">（3）生命周期管理：一般提供至少4种级别的支持：作用域、单例、线程、HTTP请求范围。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">大多数依赖注入工具在支持依赖倒置原则的基础上，在技术手段上实现了更多的功能，如类型的兼容转换、对依赖命名、在配置时直接传入对象等。</p><h3 style=\"border: 0px; margin: -8px 0px 20px; padding: 0px; font-size: 20px; font-stretch: normal; line-height: 30px; font-family: &quot;Microsoft YaHei&quot;, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(0, 0, 0);\">三、Spring依赖注入的要点</h3><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">Bean在Spring中就是POJO（.NET的POCO）。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">Spring依赖注入需要掌握的核心是3个类型BeanDefinition、BeanFactory和ApplicationContext。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">1.BeanFactory</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">BeanFactory是spring中依赖注入的核心接口，其设计主要采用了ISP（接口隔离原则），通过多层次的接口继承即保证了单个接口的内聚又保证了整个体系的简洁。这里我们要关注的核心是DefaultListableBeanFactory。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><img class=\"aligncenter\" src=\"http://images2015.cnblogs.com/blog/194803/201512/194803-20151223225630812-541500344.png\" alt=\"\" style=\"margin: 0px; padding: 0px; font-size: 0px; color: transparent; text-align: center; max-width: 100%; height: auto;\"></p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">如图所示，查看XmlBeanFactory代码，可以看到XmlBeanFactory只是通过XmlBeanDefinitionReader载入了BeanDefinition配置，XmlBeanDefinitionReader负责将配置解析到BeanDefinition。DefaultListableBeanFactory是真正的实现类，其中定义了类型为Map&lt;String, BeanDefinition&gt;的beanDefinitionMap列表用于存储依赖配置。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">2.BeanDefinition：</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">BeanDefinition定义了配置元数据，无论使用java code、xml、Annotation还是Groovy脚本方式，不同的配置方式通过不同的BeanDefinitionReader解析为BeanDefinition。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><img class=\"aligncenter\" src=\"http://images2015.cnblogs.com/blog/194803/201512/194803-20151225090249156-1756505149.png\" alt=\"\" style=\"margin: 0px; padding: 0px; font-size: 0px; color: transparent; text-align: center; max-width: 100%; height: auto;\"></p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">3.ApplicationContext</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">ApplicationContext的核心都是将对象工厂功能委托给BeanFactory的实现类DefaultListableBeanFactory。目前最常用的是基于注解的AnnotationConfigApplicationContext和AnnotationConfigWebApplicationContext。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><img class=\"alignnone\" src=\"http://images2015.cnblogs.com/blog/194803/201512/194803-20151229164411354-919999201.png\" alt=\"\" style=\"margin: 0px; padding: 0px; font-size: 0px; color: transparent; max-width: 100%; height: auto;\"></p><h3 style=\"border: 0px; margin: -8px 0px 20px; padding: 0px; font-size: 20px; font-stretch: normal; line-height: 30px; font-family: &quot;Microsoft YaHei&quot;, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(0, 0, 0);\">四、Spring依赖注入快速上手</h3><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">1.使用Java配置代替xml配置</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">Java配置的核心是@Configuration和@Bean。定义生命周期使用@Scope，需要引入其他配置文件时使用@Import。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">（1）@Configuration：应用了@Configuration注解的POCO成为了配置类。相当于xml配置文件。</p><p style=\"border: 0px; margin-bottom: 20px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">（2）@Bean：配置类中应用了@Bean注解的方法成为了配置项。相当于xml中的Bean节点。</p><div style=\"border: 0px; margin: 0px; padding: 0px; font-size: 14px; color: rgb(0, 0, 0); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\"><div style=\"border: 0px; margin: 0px; padding: 0px;\"><div id=\"highlighter_120512\" class=\"syntaxhighlighter notranslate java\" style=\"border: 0px; width: 620px; margin: 1em 0px !important; padding: 1px !important; font-size: 12px !important; position: relative !important; overflow-x: auto !important; overflow-y: hidden !important; font-family: Consolas, &quot;Bitstream Vera Sans Mono&quot;, &quot;Courier New&quot;, Courier, monospace !important;\"><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"width: 790.4px; border: 0px !important; margin: 0px !important; padding: 0px !important; border-radius: 0px !important; background-image: none !important; background-position: initial !important; background-size: initial !important; background-repeat: initial !important; background-attachment: initial !important; background-origin: initial !important; background-clip: initial !important; bottom: auto !important; float: none !important; height: auto !important; left: auto !important; line-height: 1.1em !important; outline: 0px !important; overflow: visible !important; position: static !important; right: auto !important; top: auto !important; vertical-align: baseline !important; box-sizing: content-box !important; min-height: auto !important;\"><tbody style=\"border: 0px !important; margin: 0px !important; padding: 0px !important; border-radius: 0px !important; background: none !important; bottom: auto !important; float: none !important; height: auto !important; left: auto !important; line-height: 1.1em !important; outline: 0px !important; overflow: visible !important; position: static !important; right: auto !important; top: auto !important; vertical-align: baseline !important; width: auto !important; box-sizing: content-box !important; min-height: auto !important;\"><tr style=\"border: 0px !important; margin: 0px !important; padding: 0px !important; border-radius: 0px !important; background: none !important; bottom: auto !important; float: none !important; height: auto !important; left: auto !important; line-height: 1.1em !important; outline: 0px !important; overflow: visible !important; position: static !important; right: auto !important; top: auto !important; vertical-align: baseline !important; width: auto !important; box-sizing: content-box !important; min-height: auto !i